        setValue(event.target.value)
        console.log(value)
    }

    return (
        <div className="text-field">
            <label>
                {props.label}
            </label>
            <input value={value} onChange={getValue} required={props.required} placeholder={placeholderModify}/>
        </div>
    )
}

export default TextField
